function checkForStuckPauseStatus() {
    var isPaused = gs.isPausedForAtfPerformance();
    if (!isPaused) {
        gs.info("ATFPerformancePauseReaper: Not resuming system because it is not paused");
        return;
    }

    var maxStuckTime = GlideProperties.getInt("sn_atf.performance.max_pause_idle_time", 6);

    var gr = new GlideRecord("sys_atf_test_result_performance");
    gr.addQuery("sys_created_on", ">=", gs.hoursAgo(maxStuckTime)).addOrCondition("end_time", ">=", gs.hoursAgo(maxStuckTime));
    gr.query();
    if (gr.hasNext()) {
        gs.info("ATFPerformancePauseReaper: Not resuming system because a performance test was inserted or completed in the last " + maxStuckTime + " hours");
        return;
    }

    gs.info("ATFPerformancePauseReaper: Attempting to resume system due to no active performance run");
    sn_atf.PerformanceAPI.resumeForAtfPerformance();
}

