function setMIDVersion() {
    // analyze our situation...
    var newVersion = getBuildVersion();
    var oldVersion = gs.getProperty('mid.version');

    // if we've got a new version, then we need to change some stuff...
    if (newVersion && (oldVersion != newVersion)) {

        // no matter what, we need to change the mid.version property to the new value...
        gs.setProperty('mid.version', newVersion);
        gs.log('Set mid.version to ' + newVersion);

        // update MID Upgrade history for the new version
        setMIDUpgradeHistoryInfo();

        // if we had a mid.version.override property, we need to clear it...
        if (gs.getProperty('mid.version.override') != null) {
            gs.setProperty('mid.version.override', '');
            gs.log('Clearing mid.version.override');
        }
    }

    /*
     * Returns the MID server version for this build, or null if there isn't one.
     */
    function getBuildVersion() {
        try {
            return gs.getProperty('mid.buildstamp');
        } catch (e) {
            gs.log('Skipping \'mid.version\' property update.');
            return null;
        }
    }

    function setMIDUpgradeHistoryInfo() {
        // Create Upgrade History record
        var upgradeGr = new GlideRecord('ecc_agent_upgrade_history');
        upgradeGr.initialize();
        upgradeGr.setValue('old_version', oldVersion);
        upgradeGr.setValue('new_version', newVersion);
        upgradeGr.insert();

    }

}

