function purgeDraftEmail() {
    gs.info('Running Purge Draft Email Scheduled Job');
    var emailDraftRecord = new GlideRecord("sys_email_draft");
    emailDraftRecord.addQuery("sys_mod_count", 0);
    emailDraftRecord.addEncodedQuery("sys_created_onBETWEENjavascript:gs.beginningOfLast30Days()@javascript:gs.beginningOfLast7Days()");

    var gr_attachment = new GlideRecord('sys_email_draft_attachment');
    gr_attachment.addEncodedQuery("sys_created_on>=javascript:gs.beginningOfLast30Days()");  
    gr_attachment.query();
    if(gr_attachment.getRowCount() == 0){
    	emailDraftRecord.query();
    }else{
    	var ids = [];
    	var count = 1;
    	while (gr_attachment.next()) {
        	ids.push(gr_attachment.getValue("email_draft"));
        	count++;
        	if(count == 500){
              		emailDraftRecord.addQuery("sys_id", "NOT IN", ids);
              		emailDraftRecord.query();
              		count = 1;
              		ids = [];
        	}
    	}
    	if (ids.length > 0) {
        	emailDraftRecord.addQuery("sys_id", "NOT IN", ids);
        	emailDraftRecord.query();
    	}
