function aggregateAgentZeroMetrics() {
    this.AGGREGATION_TABLE = 'ml_agent_zero_aggregated_data';
    this.SYNCED = 'synced';
    this.WAIT_TIME = 30000; // 30 seconds
    this.NUMBER_OF_TRY = 3;

    gs.info('Running Aggregate AgentZero Metrics Scheduled Job');

    // query current scheduled job record
    var gr = new GlideRecord('sys_trigger');
    gr.addQuery('name', 'Aggregate AgentZero Metrics');
    gr.query();
    gr.next();

    // get duration (in days) from 
    var gdt1 = new GlideDateTime('1970-01-01 00:00:00');
    var gdt2 = new GlideDateTime(gr.getValue("repeat"));
    var days = GlideDateTime.subtract(gdt1, gdt2).getDayPart();

    try {
        // aggregate records using Aggregation API 
        var aggregationAPI = new global.MLAgentZeroAggreationAPI(days);
        var result = aggregationAPI.aggregateRecords();

        try {
            // solution names of aggregated solution versions are sent for selftuning
            sn_ml.AgentZeroSelfTuning.selfTune(result["solutionNameList"]);
        }
        catch (error) {
            gs.error('Error AgentZero selftuning: ' + error);
        }

        var successful = false;
        var aggRecord = new GlideRecord(this.AGGREGATION_TABLE);
        aggRecord.addQuery(this.SYNCED, false);
        aggRecord.setLimit(1);
        aggRecord.query();
        // check if there is any quality metrics record to sysnc
        if (aggRecord.next()) {
            if (result["solutionNameList"].length > 0) {
                // try multiple times to sync quality metric records
                for (var i = 0; i < this.NUMBER_OF_TRY; i++) {
                    try {
                        // make dummy prediction if there is an unsync record
                        var solutionVersion = sn_ml.AgentZeroSolutionStore.get(result["solutionNameList"][0]).getActiveVersion();
                        var options = { compositeQualityMetics: true };
                        var predictionResult = solutionVersion.predict([{ 'short_description': 'Facing issue with printer on 3rd floor' }], options);
                        gs.info(JSON.stringify(JSON.parse(predictionResult), null, 2));

                        var json = JSON.parse(predictionResult);
                        // check if the prediction is successful
                        if (json.hasOwnProperty('1')) {
                            successful = true;
                            break;
                        }
                    } catch (e) {
                        gs.error(e);
                    }
                    // wait WAIT_TIME millisecond for the next try
                    gs.sleep(this.WAIT_TIME);
                    gs.info('Aggregate AgentZero Metrics Scheduled Job number of try to sync quality metrics: ' + (i + 1));
                }
                if (successful == false) {
                    gs.info('Aggregate AgentZero Metrics Scheduled Job was not able to sync quality metrics');
                }
                else {
                    // clean old records only after a successful sync of records
                    aggregationAPI.cleanAggregatedDataTable();
                }
            }
        }
    } catch (err) {
        gs.error('Error aggregating AgentZero metrics: ' + err);
    }
}

