function aggregateJobHistory() {
	const now = new GlideDateTime();
	now.subtract(120000); // 2 minute buffer
	const lastRangeSynced = getLastSynced();
	const gr = new GlideRecord("sys_scheduler_job_history_node");
	gr.addQuery("time_range_end", ">", lastRangeSynced);
	gr.addQuery("time_range_end", "<", now);
	gr.orderBy("time_range_end");
	gr.orderBy("job_classification");
	gr.orderBy("priority");
	gr.query();
	if (gr.getRowCount() == 0)
		return;
	var currentJobClassification = "";
	var currentPriority = -1;
	var currentTimeRangeEnd = null;
	var instanceGR = null;
	while (gr.next()) {
		debug("queried " + gr.getValue("node_name") + " " + gr.getValue("job_classification") + " " + gr.getValue("time_range_end"));
		var jobClassification = gr.getValue("job_classification");
		var priority = gr.getValue("priority");
		var timeRangeEnd = new GlideDateTime(gr.getValue("time_range_end"));
		if (instanceGR != null && (jobClassification != currentJobClassification || priority != currentPriority || !timeRangeEnd.equals(currentTimeRangeEnd))) {
			debug("inserting record into sys_scheduler_job_history");
			instanceGR.insert();
			instanceGR = null;
		}
		currentJobClassification = jobClassification;
		currentPriority = priority;
		currentTimeRangeEnd = timeRangeEnd;
		if (instanceGR == null) {
			debug(" new GR for sys_scheduler_job_history - " + currentJobClassification);
			instanceGR = new GlideRecord("sys_scheduler_job_history");
			instanceGR.setValue("job_classification", currentJobClassification);
			instanceGR.setValue("priority", priority);
			instanceGR.setValue("count", gr.getValue("count"));
			instanceGR.setValue("total", gr.getValue("total"));
			instanceGR.setValue("average", gr.getValue("average"));
			instanceGR.setValue("max_duration", gr.getValue("max_duration"));
			instanceGR.setValue("total_lateness", gr.getValue("total_lateness"));
			instanceGR.setValue("avg_lateness", gr.getValue("avg_lateness"));
			instanceGR.setValue("max_lateness", gr.getValue("max_lateness"));
			instanceGR.setValue("time_range_start", new GlideDateTime(gr.getValue("time_range_start")));
			instanceGR.setValue("time_range_end", currentTimeRangeEnd);
		} else {
			aggregate(gr, instanceGR);
		}
	}
	if (instanceGR != null) {
		debug("inserting record into sys_scheduler_job_history");
		instanceGR.insert();
		instanceGR = null;
	}
}

function aggregate(gr, instanceGR) {
	debug("aggregating");
	var count = Number(instanceGR.getValue("count")) + Number(gr.getValue("count"));
	var total = Number(instanceGR.getValue("total")) + Number(gr.getValue("total"));
	var totalLateness = Number(instanceGR.getValue("total_lateness")) + Number(gr.getValue("total_lateness"));

	instanceGR.setValue("count", count);
	instanceGR.setValue("total", total);
	instanceGR.setValue("average", total / count);
	instanceGR.setValue("max_duration", Math.max(instanceGR.getValue("max_duration"), gr.getValue("max_duration")));

	instanceGR.setValue("total_lateness", totalLateness);
	instanceGR.setValue("avg_lateness", totalLateness / count);
	instanceGR.setValue("max_lateness", Math.max(instanceGR.getValue("max_lateness"), gr.getValue("max_lateness")));
}

