function cleanPDMonitorableItems() {
  var monitorableItemRecord = new GlideRecord('snc_monitorable_item');
  if (!monitorableItemRecord.isValid())
    return;

  monitorableItemRecord.query();
  while(monitorableItemRecord.next()) {
    if (hasJrobinDBRecord(monitorableItemRecord.sys_id))
      continue;

    gs.warn('Deleting orphaned \'monitorable item\': ' + monitorableItemRecord.name);
    monitorableItemRecord.deleteRecord();
  }
}

