(function executeIt() {

var max_rows = 10; // batch should be less than 2K to  avoid throttling
var scopes = getScopes();

var streamId1 = "snc.table.collector";
var opened = AnalyticsFramework.open(streamId1);
if (opened == 4 || opened == 0) {
    try {
        streamData(scopes, rawSend);
    } finally {
        AnalyticsFramework.close(streamId1);
    }
}

function rawSend(rows) {
    if (Object.keys(rows).length > 0) {
        //gs.log(JSON.stringify(rows, null, 2));

        var obj = {};
        obj["event.type"] = "usage.table.custom";
        obj['data'] = rows;
        var status = AnalyticsFramework.sendJSON(streamId1, JSON.stringify(obj));
        if (status != 0)
            gs.warn("Failed to send scoped app information with status" + status);
    }
}

function getScopes() {
    var scopes = {};
    var gr = new GlideRecord('sys_custom_db_object');
    gr.addQuery('sys_created_on', '>=', gs.daysAgo(1));
    gr.query();
    while (gr.next()) {
        var scope = gr.source_scope.name.toString();
        if (!(scope in scopes)) {
            scopes[scope] = {};
            scopes[scope]['tables'] = {};
            scopes[scope]['n_tables'] = 0;
        }
        var src_t = gr.getValue('source_type');
        var table = "";
        if (src_t == 'table') {
            table = gr.getValue('source');
        } else {
            table = gr.getValue('source_parent');
        }

        var tables = scopes[scope]['tables'];
        if (!(table in tables)) {
            tables[table] = {};
            tables[table]['n_columns'] = 0;
            ++scopes[scope]['n_tables'];
        }

        if (src_t == 'table') {
            var v = getBaseTable(table);
            if (v != null)
                tables[table]['base'] = v;
        } else {
            ++tables[table]['n_columns'];
        }
    }

    return scopes;
}


function streamData(scopes, sendCallBack) {
    var rows = {};
    for (var s in scopes) {
        var tables = scopes[s]['tables'];
        for (var t in tables) {
            var sys_id = SncTableEditor.getTokenSysId(t);
            rows[sys_id] = scopes[s]['tables'][t];
            rows[sys_id]['scope'] = s;
            //rows[sys_id]['name'] = t;
            if (Object.keys(rows).length >= max_rows) {
                sendCallBack(rows);
                rows = {};
            }
        }
    }

    sendCallBack(rows);
}

function getBaseTable(tableName) {
    var tables = GlideDBObjectManager.get().getTables(tableName);
    for (i = 0; i < tables.size(); i++) {
        var t = tables.get(i);
        if (t != tableName && !t.startsWith("u_") && !t.startsWith("x_"))
            return t;
    }

    return null;
}


