function buildContextActions() {
   var counts = gs.getProperty('glide.ui.per_page', '10,20,50,100');
   var items = counts.split(',');
   var selection = gs.getPreference('rowcount', '20');
   var label = gs.getMessage('rows per page');
   for (var i = 0; i < items.length; i++) {
      var item = items[i];
      var img = "";
      if (item == selection)
         img = 'images/checked.pngx';
         
      g_contextMenu.addAction(item, item + ' ' + label, 'showRowsPerPage("' + item + '");', item, img);
   }
}

