/**
 * Hereinafter the configuration for the UIB side panel properties
 * This function is being called by the now-ui json[scriptedConfigPolicy]
 * @param api - Contains the methods to controls the state of the properties
 * @param trigger - The current property changed action
 * @param properties - The properties and their values
 */

function policy(args) { //eslint-disable-line no-unused-vars
	const {api, trigger, properties} = args;
	const isAttachment = properties.documentType === 'attachment';
	const isHTML = properties.documentType === 'html';
	const enableEditing = properties.enableEditing === true;
	const resizable = properties.resizable === true;

	function updateAttachmentDependents(args) {
		const {api, properties} = args;
		const showToolbarProperties = properties.documentType === 'attachment' && !properties.hideToolbar;
		const isPdf = properties.documentType === 'attachment' && properties.attachmentType === 'pdf';
		const isImage = properties.documentType === 'attachment' && properties.attachmentType === 'image';
		api.updateVisibility('hideDownload', showToolbarProperties);
		api.updateVisibility('hideDelete', showToolbarProperties);
		api.updateVisibility('hideClose', showToolbarProperties);
		api.updateVisibility('hideSearch', isPdf && showToolbarProperties);
		api.updateVisibility('hideNavigation', isPdf && showToolbarProperties);
		api.updateVisibility('hideZoom', isPdf && showToolbarProperties);
		api.updateVisibility('hideRotation', isPdf && showToolbarProperties);

		if (isPdf) {
			api.updateTypeMetadata('attachmentId', 'reference', {
				table: 'sys_attachment',
				referenceFilter: 'content_type=application/pdf'
			});
		} else if (isImage) {
			api.updateTypeMetadata('attachmentId', 'reference', {
				table: 'sys_attachment',
				referenceFilter: 'content_typeSTARTSWITHimage/'
			});
		}
	}

	if (!trigger || trigger === 'documentType') {
		api.updateVisibility('attachmentType', isAttachment);
		api.updateVisibility('attachmentId', isAttachment);
		api.updateVisibility('hideToolbar', isAttachment);
		updateAttachmentDependents(args);
		api.updateVisibility('htmlContent', isHTML);
		api.updateVisibility('enableEditing', isHTML);
		api.updateVisibility('allowBlockBasedAnchors', isHTML);
	}

	if (!trigger || trigger === 'hideToolbar' || trigger === 'attachmentType')
		updateAttachmentDependents(args);

	if (!trigger || trigger === 'documentType' || trigger === 'enableEditing') {
		api.updateVisibility('editorHeight', isHTML && enableEditing);
		api.updateVisibility('resizable', isHTML && enableEditing);
	}

	if (!trigger || trigger === 'documentType' || trigger === 'enableEditing' || trigger === 'resizable') 
		api.updateVisibility('editorMaxHeight', isHTML && enableEditing && resizable);
}

// This file sets logical behavior for the now-list config panel in UIB
// e.g. disabling properties that depending on other props, etc

//eslint-disable-next-line no-unused-vars
function nowListPolicy(/** @type {any} */ { api, trigger, properties }) {
	// Default display

	if (trigger === 'columnDefinitions') {
		api.updateEnabled(
			'rowDefinitions',
			!(Object.keys(properties.columnDefinitions).length === 0)
		);
		api.updateSectionVisibility(
			'featureFlags',
			!(Object.keys(properties.columnDefinitions).length === 0)
		);
	}
	if (trigger === 'rowDefinitions') {
		const rowDependencies = [
			'expandAllGroups',
			'rowDragDropEnabled',
			'selectionEnabled',
			'cellEditingEnabled',
			'cellEditingManaged',
		];
		rowDependencies.map((propName) => {
			api.updateVisibility(
				propName,
				!(Object.keys(properties.rowDefinitions).length === 0)
			);
		});
	}
	if (trigger === 'cellEditingEnabled') {
		api.updateVisibility(
			'cellEditingManaged',
			properties.cellEditingEnabled
		);
	}
	if (trigger === 'selectionEnabled') {
		api.updateVisibility('selectAllEnabled', properties.selectionEnabled);
	}
}

export default nowListPolicy;

/**
 * This function is inlined by Tectonic to UIB metadata and is used by UIB
 * to perform custom layout tweaks of component config panel.
 * It is associated with target component in `now-ui.json -> <component> -> uiBuilder -> scriptedConfigPolicy`.
 *
 * This particular function is intended to be used by config panel of `sn-promin-process-map-connected` component.
 * @type {UIB.PolicyInvoker<SnProminProcessMapConnected.Props>}
 **/
// eslint-disable-next-line no-unused-vars, @typescript-eslint/no-unused-vars
function applyPolicies({ api, properties, trigger }) {
	const { projectId, starDiagramCenterNode, diagramType } = properties;

	// Set metadata for our custom UI Config panel component so it receives it as props
	// Component: sn-promin-uib-custom-value-selector
	api.updateInterfaceMetadata('starDiagramCenterNode', 'projectId', projectId);
	api.updateInterfaceMetadata('starDiagramCenterNode', 'propName', 'starDiagramCenterNode');
	api.updateInterfaceMetadata('starDiagramCenterNode', 'propValue', starDiagramCenterNode);

	const isProjectSelected = Boolean(projectId) || api.isDynamic('projectId');
	const isProcessGraph = isProjectSelected && (diagramType === 'process' || api.isDynamic('diagramType'));
	const isStarGraph = isProjectSelected && (diagramType === 'star' || api.isDynamic('diagramType'));

	// Set visibility for properties conditionally on project selection or diagram type
	api.updateVisibility('diagramType', isProjectSelected);
	api.updateVisibility('followFilters', isProjectSelected);
	api.updateVisibility('showNodeArcPopupActions', isProjectSelected);
	api.updateVisibility('enableCaseFollowSimulation', isProcessGraph);
	api.updateVisibility('edgePercentage', isProcessGraph);
	api.updateVisibility('nodePercentage', isProcessGraph);
	api.updateVisibility('starDiagramCenterNode', isStarGraph);
	api.updateVisibility('processGraphOrientation', isProcessGraph);

	// Update values for related properties after update of trigger property
	if (trigger === 'projectId') {
		api.updateValue('starDiagramCenterNode', null);
		api.updateValue('diagramType', 'process');
	}
	if (trigger === 'diagramType')
		api.updateValue('starDiagramCenterNode', null);

	// Apply backward compatibility fix for renamed property 'title' to 'headerTitle'
	// In fact we still have the old property 'title' in the component, but it is invisible
	// And its value is automatically copied to new property 'headerTitle' in case it is empty
	api.updateVisibility('title', false);
	if (properties.title && !properties.headerTitle) {
		api.updateValue('headerTitle', properties.title);
		api.updateValue('title', '');
	}
}

