function policy(args) {
	var trigger = args.trigger;
	var api = args.api;
	var properties = args.properties;
	var sourceType = parseInt(properties.sourceType);
	var scorecardType = properties.scorecardType;

	function getParsedJson(entity) {
		return typeof entity === 'string' ? JSON.parse(entity) : entity;
	}

	/*
	 *Hide 'Show only breakdowns' field from config panel when,
	 *one of the following conditions are met:
	 *1. Source Type is not Manual
	 *2. Scorecard Type is not of type list
	 *3. More than one or no indicators are configured
	 *4. No breakdowns are configured
	 */
	function hideBreakdownsOnlyProperty(properties) {
		var indicators = properties.indicators;
		var breakdowns = properties.breakdowns;
		var scorecardType = properties.scorecardType;

		return (
			sourceType !== 1 ||
			scorecardType !== 'list' ||
			getParsedJson(indicators).length !== 1 ||
			getParsedJson(breakdowns).length === 0
		);
	}

	function hideBreakdownsAsColumns(properties) {
		return (
			properties.scorecardType !== 'pivot' ||
			getParsedJson(properties.breakdowns).length === 0
		);
	}

	function showBreakdownsAsColumns(isVisible) {
		api.updateVisibility('swapBreakdownsAsColumns', isVisible);
	}

	function resetSwapBreakdownsAsColumns() {
		api.updateValue('swapBreakdownsAsColumns', false);
	}

	function indicator(isVisible) {
		api.updateVisibility('par-scorecard-indicators', isVisible);
	}

	function indicatorGroup(isVisible) {
		api.updateVisibility('indicatorGroup', isVisible);
	}

	function indicatorQuery(isVisible) {
		api.updateVisibility('query', isVisible);
	}

	function isShowAllEnabled(breakdowns) {
		return breakdowns?.[0]?.perPage === 'ALL';
	}

	function isPerPageGreaterThanLimit(breakdowns) {
		return breakdowns?.[0]?.perPage > 20;
	}

	function resetIndicatorQuery() {
		api.updateValue('query', 'display=true^benchmarking=false');
	}

	function resetIndicators() {
		api.updateValue('indicators', []);
	}

	function resetIndicatorGroup() {
		api.updateValue('indicatorGroup', '');
	}

	function toggleRuntimeBreakdownProps(value) {
		api.updateValue('allowChangeBreakdown', value);
		api.updateVisibility('allowChangeBreakdown', value);
		api.updateValue('breakdownVisibilitySelector', value);
		api.updateVisibility('breakdownVisibilitySelector', value);
	}

	function disableRunTimeBreakdownProps(properties) {
		var indicators = getParsedJson(properties.indicators);
		var breakdowns = getParsedJson(properties.breakdowns);
		var dsFilters = indicators?.[0]?.breakdowns;
		if (
			dsFilters?.length > 0 &&
			(dsFilters.length > 1 || breakdowns.length > 1)
		) {
			api.updateValue('allowChangeBreakdown', false);
			api.updateEnabled('allowChangeBreakdown', false);
		} else {
			api.updateValue('allowChangeBreakdown', properties.allowChangeBreakdown);
			api.updateEnabled('allowChangeBreakdown', true);
		}
	}

	function updateBreakdownsPerPage(breakdowns, api) {
		var MAX_LIMIT = 20;
		var perPage = breakdowns[0].perPage;
		var updatedBreakdowns = breakdowns.map((breakdown) => {
			return {
				...breakdown,
				perPage: perPage == 'ALL' || perPage > MAX_LIMIT ? MAX_LIMIT : perPage,
			};
		});
		api.updateValue('breakdowns', getParsedJson(updatedBreakdowns));
	}

	function handleSortingOptions() {
		const ALL_SORT_BY_OPTIONS = [
			{ label: 'Default', value: 'default' },
			{ label: 'Name', value: 'name' },
			{ label: 'Last score', value: 'score' },
			{ label: 'Target', value: 'target' },
			{ label: 'Change', value: 'change' },
			{ label: 'Change %', value: 'changePercent' },
			{ label: 'Gap', value: 'gap' },
			{ label: 'Gap %', value: 'gapPercent' },
			{ label: 'Group order', value: 'indicator_group' },
		];

		const filteredSortByOptions = ALL_SORT_BY_OPTIONS.filter((sortOption) =>
			sortOption.value === 'default' ? sourceType === 1 : true
		);

		const hasCurrentSortBy = filteredSortByOptions.find(
			(option) => option.value === properties?.sortBy
		);

		if (!hasCurrentSortBy) {
			api.updateValue('sortBy', 'name');
		}

		if (trigger === 'sourceType' && sourceType === 1) {
			api.updateValue('sortBy', 'default');
		}

		api.updateTypeMetadata('sortBy', 'choices', filteredSortByOptions);
	}

	function hasBreakdownFilters() {
		return properties.breakdowns?.some(
			(breakdown) => breakdown?.elementIds?.length > 0
		);
	}

	function convertBreakdownToDataSourceFilters() {
		var updatedIndicators = properties.indicators;

		properties.breakdowns.forEach(({ breakdownId, elementIds }) => {
			if (elementIds?.length > 0) {
				const newIndicatorBreakdown = {
					sysId: breakdownId,
					elementSysIds: [...elementIds],
				};

				updatedIndicators = updatedIndicators.map(
					({ breakdowns, ...indicator }) => {
						const existingBreakdownIndex = breakdowns?.findIndex(
							({ sysId }) => breakdownId === sysId
						);

						if (existingBreakdownIndex >= 0) {
							const newElementSysIds = [
								...new Set([
									...newIndicatorBreakdown.elementSysIds,
									...breakdowns[existingBreakdownIndex].elementSysIds,
								]),
							];

							breakdowns[
								existingBreakdownIndex
							].elementSysIds = newElementSysIds;
						} else if (breakdowns) {
							breakdowns.push(newIndicatorBreakdown);
						} else {
							breakdowns = [newIndicatorBreakdown];
						}

						return {
							...indicator,
							breakdowns,
						};
					}
				);
			}
		});

		api.updateValue('indicators', updatedIndicators);
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'indicators',
			updatedIndicators
		);

		var updatedBreakdowns = properties.breakdowns.map(
			({ elementIds, ...breakdown }) => {
				return {
					...breakdown,
					elementIds: [],
				};
			}
		);
		api.updateValue('breakdowns', updatedBreakdowns);
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'breakdowns',
			updatedBreakdowns
		);
	}

	const { hideDevSettings } = properties;
	api.updateVisibility('headingLevel', !hideDevSettings);
	api.updateVisibility('enableDrilldown', !hideDevSettings);

	// Show/Hide cache props
	api.updateVisibility('useDataCache', !hideDevSettings);
	api.updateVisibility(
		'cacheExpirationTime',
		Boolean(api.isDynamic('useDataCache') || properties.useDataCache)
	);
	api.updateVisibility('showCacheTime', Boolean(api.isDynamic('useDataCache') || properties.useDataCache));

	if (!trigger || trigger === 'sourceType') {
		handleSortingOptions();
		if (sourceType === 0) {
			indicatorQuery(true);
			indicator(false);
			indicatorGroup(false);

			resetIndicators();
			resetIndicatorGroup();
		} else if (sourceType === 1) {
			indicatorQuery(false);
			indicator(true);
			indicatorGroup(false);

			resetIndicatorQuery();
			resetIndicatorGroup();
		} else if (sourceType === 2) {
			indicatorQuery(false);
			indicator(false);
			indicatorGroup(true);

			resetIndicatorQuery();
			resetIndicators();
		}
	}

	if (
		!trigger ||
		trigger === 'indicators' ||
		trigger === 'breakdowns' ||
		trigger === 'scorecardType' ||
		trigger === 'sourceType'
	) {
		// Toggle the visibility of the 'Show only breakdowns' field
		// and its value based on the configuration
		if (hideBreakdownsOnlyProperty(properties)) {
			api.updateValue('showBreakdownsOnly', false);
			api.updateVisibility('showBreakdownsOnly', false);
			toggleRuntimeBreakdownProps(false);
			api.updateInterfaceMetadata(
				'par-scorecard-metrics',
				'showBreakdownsOnly',
				false
			);
		} else {
			api.updateValue('showBreakdownsOnly', properties.showBreakdownsOnly);
			api.updateVisibility('showBreakdownsOnly', true);
			api.updateInterfaceMetadata(
				'par-scorecard-metrics',
				'showBreakdownsOnly',
				properties.showBreakdownsOnly
			);
			disableRunTimeBreakdownProps(properties);
		}
	}

	if (!trigger || trigger === 'showBreakdownsOnly') {
		api.updateInterfaceMetadata(
			'par-scorecard-metrics',
			'showBreakdownsOnly',
			properties.showBreakdownsOnly
		);

		if (properties.showBreakdownsOnly === true) {
			api.updateValue('allowChangeBreakdown', properties.allowChangeBreakdown);
			api.updateVisibility('allowChangeBreakdown', true);
			disableRunTimeBreakdownProps(properties);
		} else {
			toggleRuntimeBreakdownProps(false);
		}
	}
	if (!trigger || trigger === 'allowChangeBreakdown') {
		if (properties.allowChangeBreakdown === true) {
			api.updateValue(
				'breakdownVisibilitySelector',
				properties.breakdownVisibilitySelector
			);
			api.updateVisibility('breakdownVisibilitySelector', true);
		} else {
			api.updateValue('breakdownVisibilitySelector', false);
			api.updateVisibility('breakdownVisibilitySelector', false);
		}
	}

	if (
		(!trigger ||
			trigger === 'indicators' ||
			trigger === 'scorecardType' ||
			trigger === 'sourceType') &&
		scorecardType === 'list' &&
		sourceType === 1
	) {
		api.updateVisibility(
			'pageSize',
			!(getParsedJson(properties.indicators).length <= 10)
		);
	} else {
		api.updateVisibility('pageSize', true);
	}

	if (
		!trigger ||
		trigger === 'showBreakdownsOnly' ||
		trigger === 'scorecardType' ||
		trigger === 'swapBreakdownsAsColumns' ||
		trigger === 'breakdowns'
	) {
		var showAllElementsOption = false;
		if (
			(properties.scorecardType === 'list' && properties.showBreakdownsOnly) ||
			(properties.scorecardType === 'pivot' &&
				!properties.swapBreakdownsAsColumns)
		) {
			showAllElementsOption = true;
			// Show PageSize when first breakdown has maximum number of Groups as 'ALL'
			api.updateVisibility('pageSize', isShowAllEnabled(properties.breakdowns));
		} else {
			// If maximum number of groups for first breakdown is ALL or more than 20 and breakdowns are not used in rows, then reset maximum number of groups to 20.
			if (
				isShowAllEnabled(properties.breakdowns) ||
				isPerPageGreaterThanLimit(properties.breakdowns)
			) {
				updateBreakdownsPerPage(properties.breakdowns, api);
			}
		}
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'showAllElementsOption',
			showAllElementsOption
		);
	}

	if (
		!trigger ||
		trigger === 'breakdowns' ||
		trigger === 'scorecardType' ||
		trigger === 'swapBreakdownsAsColumns'
	) {
		if (hideBreakdownsAsColumns(properties)) {
			showBreakdownsAsColumns(false);
			resetSwapBreakdownsAsColumns();
		} else {
			showBreakdownsAsColumns(true);

			// If swapBreakdownsAsColumns is toggled on then keep only level 1 breakdown
			if (properties.swapBreakdownsAsColumns) {
				var breakdowns = properties.breakdowns;

				if (breakdowns && breakdowns.length > 1) {
					// Since only level 1 breakdowns are supported, removing other breakdown applied.
					api.updateValue('breakdowns', [getParsedJson(breakdowns)[0]]);
				}
			}
		}

		// Disable pageSize change on switch to pivot scorecard
		// Toggle the visibility of Show Favorite and Hide Indicator Info fields, for pivot scorecard.
		if (properties.scorecardType === 'pivot') {
			api.updateValue('pageSize', '10');
			api.updateVisibility('showFavorite', false);
			api.updateVisibility('showIndicatorInfo', false);
			//Enable PageSize when first breakdown has maximum number of Groups as 'ALL' and breakdowns used as row
			api.updateEnabled(
				'pageSize',
				!properties.swapBreakdownsAsColumns &&
					isShowAllEnabled(properties.breakdowns)
			);
		} else {
			api.updateEnabled('pageSize', true);
			api.updateVisibility('showFavorite', true);
			api.updateVisibility('showIndicatorInfo', true);
		}
	}

	if (!trigger || trigger === 'swapBreakdownsAsColumns') {
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'swapBreakdownsAsColumns',
			properties.swapBreakdownsAsColumns
		);
	}

	if (!trigger || trigger === 'indicatorGroup') {
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'indicatorGroup',
			properties.indicatorGroup
		);
		api.updateInterfaceMetadata(
			'par-scorecard-aggregates',
			'indicatorGroup',
			properties.indicatorGroup
		);
	}

	if (!trigger || trigger === 'query') {
		api.updateInterfaceMetadata('par-breakdowns', 'query', properties.query);
		api.updateInterfaceMetadata(
			'par-scorecard-aggregates',
			'query',
			properties.query
		);
	}

	if (!trigger || trigger === 'indicators') {
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'indicators',
			properties.indicators
		);
		api.updateInterfaceMetadata(
			'par-scorecard-metrics',
			'indicators',
			properties.indicators
		);
		api.updateInterfaceMetadata(
			'par-scorecard-aggregates',
			'indicators',
			properties.indicators
		);
	}

	if (!trigger || trigger === 'breakdowns') {
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'breakdowns',
			properties.breakdowns
		);
		api.updateInterfaceMetadata(
			'par-scorecard-metrics',
			'breakdowns',
			properties.breakdowns
		);
		api.updateInterfaceMetadata(
			'par-scorecard-aggregates',
			'breakdowns',
			properties.breakdowns
		);
	}

	if (!trigger || trigger === 'scorecardType') {
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'scorecardType',
			properties.scorecardType
		);
		api.updateInterfaceMetadata(
			'par-scorecard-metrics',
			'scorecardType',
			properties.scorecardType
		);
		api.updateInterfaceMetadata(
			'par-scorecard-aggregates',
			'scorecardType',
			properties.scorecardType
		);
	}

	if (!trigger || trigger === 'sourceType') {
		api.updateInterfaceMetadata(
			'par-breakdowns',
			'sourceType',
			properties.sourceType
		);
		api.updateInterfaceMetadata(
			'par-scorecard-metrics',
			'sourceType',
			properties.sourceType
		);
		api.updateInterfaceMetadata(
			'par-scorecard-aggregates',
			'sourceType',
			properties.sourceType
		);
	}

	if (!trigger) {
		api.updateVisibility('showFilterIcon', properties.followFilters);
		api.updateEnabled('showFilterElement', properties.followFilters);
	}

	if (trigger === 'followFilters') {
		api.updateEnabled('showFilterElement', properties.followFilters);
		api.updateVisibility('showFilterIcon', properties.followFilters);
		api.updateValue('showFilterIcon', properties.followFilters);
	}

	if (
		sourceType === 1 &&
		properties.indicators?.length > 0 &&
		hasBreakdownFilters()
	) {
		convertBreakdownToDataSourceFilters();
	}
}

/**
 * Hereinafter the configuration for the UIB side panel properties
 * This function is being called by the now-ui json[scriptedConfigPolicy]
 * @param api - Contains the methods to controls the state of the properties
 * @param trigger - The current property changed action
 * @param properties - The properties and their values
 */
function policy({api, trigger, properties: p}) { //eslint-disable-line no-unused-vars
	/**
	 * Syncing properties that need to show/hide depending on the choosen layout.
	 */

	const choice = p['mode'];

	switch (choice) {
		case 'manual':
			api.updateVisibility('items', true);
			api.updateVisibility('includeSysId', false);
			api.updateVisibility('allowDotWalking', false);
			api.updateVisibility('tableName', false);
			api.updateVisibility('itemsRoot', true);
			api.updateVisibility('showSelectedItemPath', true);
			break;
		case 'tableDescription':
			api.updateVisibility('items', false);
			api.updateVisibility('includeSysId', true);
			api.updateVisibility('allowDotWalking', true);
			api.updateVisibility('tableName', true);
			api.updateVisibility('itemsRoot', false);
			api.updateVisibility('showSelectedItemPath', false);
			break;
		default:
			break;
	}
}

/**
 * Hereinafter the configuration for the UIB side panel properties
 * This function is being called by the now-ui json[scriptedConfigPolicy]
 * @param api - Contains the methods to controls the state of the properties
 * @param trigger - The current property changed action
 * @param properties - The properties and their values
 */
function policy(args) {
	/**
	 * In Utah, the property 'KPI Details View' (kpiDetailsView) was changed from a dropdown (choicelist of 'Chart View' and ' Records View')
	 * to a toggle property named 'showRecordsView'. We just need to make sure that the new property showRecordsView is set to the
	 * old property's (kpiDetailsView) corresponding value
	 *
	 * @param {*} api
	 * @param {*} properties
	 */
	function updateKPIDetailViewToShowRecordsView(api, properties) {
		api.updateVisibility('kpiDetailsView', false);
		api.updateValue(
			'showRecordsView',
			properties.kpiDetailsView == 'recordsView'
		);
		api.updateVisibility('recordsListView', true);
	}

	/**
	 * In Utah, the property 'statisticsOrientation' was changed to 'showHorizontalStatisticOrientation'
	 * The property 'statisticsOrientation' was a dropdown with two choices: horizontal and vertical
	 * From Utah, the new property 'showHorizontalStatisticOrientation' is a toggle where ON means Horizontal orientation
	 *
	 * @param {*} api
	 * @param {*} properties
	 */
	function updateStatisticsOrientation(api, properties) {
		api.updateVisibility('statisticsOrientation', false);
		api.updateValue(
			'showHorizontalStatisticOrientation',
			properties.statisticsOrientation == 'horizontal'
		);
	}

	/**
	 * If toggle Show Records View is on, we show the Records List View configuration
	 *
	 * @param {*} api
	 * @param {*} properties
	 */
	function showRecordsListView(api, properties) {
		api.updateVisibility('recordsListView', properties.showRecordsView);
	}

	//eslint-disable-line no-unused-vars
	const { api, trigger, properties } = args;

	if (!trigger && Object.keys(properties).includes('kpiDetailsView')) {
		updateKPIDetailViewToShowRecordsView(api, properties);
	}

	if (!trigger && Object.keys(properties).includes('statisticsOrientation')) {
		updateStatisticsOrientation(api, properties);
	}

	if (!trigger || trigger === 'showRecordsView') {
		showRecordsListView(api, properties);
	}
}

/**
 * Hereinafter the configuration for the UIB side panel properties
 * This function is being called by the now-ui json[scriptedConfigPolicy]
 * @param api - Contains the methods to controls the state of the properties
 * @param trigger - The current property changed action
 * @param properties - The properties and their values
 */

// eslint-disable-next-line no-unused-vars
function policy({ trigger, api, properties }) {
	if (trigger === 'manageReorderRows' || !trigger) {
		const isManagingReorder = properties.manageReorderRows === true;

		api.updateEnabled('disableReorderRows', isManagingReorder);
	}

	if (trigger === 'manageColumnPersonalization' || !trigger) {
		properties.manageColumnPersonalization &&
			api.setPropertyValue('manageColumnShowHide', false);
		api.updateEnabled(
			'manageColumnShowHide',
			!properties.manageColumnPersonalization
		);
	}

	if (trigger === 'enableAddColumn' || !trigger) {
		api.updateEnabled('addColumnConfig', properties.enableAddColumn === true);
	}
}

// eslint-disable-next-line no-unused-vars
function avatarPolicy({trigger, api, properties}) {
	if (trigger === null) {
		api.updateVisibility('href', properties.interaction === 'link');
		api.updateVisibility('opensWindow', properties.interaction === 'link');
	}
	if (trigger === 'interaction') {
		api.updateVisibility('href', properties.interaction === 'link');
		api.updateVisibility('opensWindow', properties.interaction === 'link');
	}
}

/**
 * This function is being called by the now-ui json[scriptedConfigPolicy] for displayValueBlock
 *
 * @param api - Contains the methods to controls the state of the properties
 */

// eslint-disable-next-line no-unused-vars
function policy(args) {
	const { api, properties, trigger } = args;
	const { hideLabels } = properties;

	if (trigger === 'hideLabels') {
		if (hideLabels) {
			api.updateVisibility('displayInline', false);
		} else {
			api.updateVisibility('displayInline', true);
		}
	}
}

