		api.updateVisibility('rowLaneConfig', properties.showSwimlaneAsRow);
	}
	if (!trigger || trigger === 'enableDependencyLines') {
		api.updateVisibility(
			'dependencyLines',
			properties.enableDependencyLines
		);
	}
}

function scriptedPolicy(args) {	
    var trigger = args.trigger;
    var properties = args.properties;
    var api = args.api;

    //initial state should be readonly for blocksInformation as showBlocksInformation value is false by default.
    api.updateEnabled('blocksInformation', properties.showBlocksInformation);

    if (trigger === 'showBlocksInformation') {
		api.updateEnabled('blocksInformation', properties.showBlocksInformation);
    }
}

/**
 * Hereinafter the configuration for the UIB side panel properties
 * This function is being called by the now-ui json[scriptedConfigPolicy]
 * @param api - Contains the methods to controls the state of the properties
 * @param trigger - The current property changed action
 * @param properties - The properties and their values
 */
function policy({ api, trigger, properties }) {
	if (!trigger || trigger === 'view') {
		var enableTimesPerRow = properties.view === 'DAY' || properties.view === 'DAYMOBILE';
		api.updateVisibility('numberOfEventsInEachRow', enableTimesPerRow);
	}
}

// eslint-disable-next-line no-unused-vars
function buttonIconicPolicy({trigger, api, properties}) {
	if (trigger === 'bare' || trigger === null) {
		api.updateVisibility('hidePadding', properties.bare);
	}

	if (trigger === 'disabled' || trigger === null) {
		api.updateVisibility('tooltipContent', !properties.disabled);
	}
}

/**
 * This function is being called by the now-ui json[scriptedConfigPolicy]
 * This will hide/show properties for the new Tab set experience in Tokyo
 *
 * @param api - Contains the methods to controls the state of the properties
 */

// eslint-disable-next-line no-unused-vars
function policy(args) {
	const { api, properties, trigger } = args;
	const { position, selectedTabIndex } = properties;

	api.updateVisibility('dynamicTabData', false);
	api.updateVisibility('defaultRoute', false);
	api.updateVisibility('items', false);

	// If selectedTabIndex was never used, hide this field.
	// This is deprecated by 'selectedTab'.
	if (selectedTabIndex === null && !api.isDynamic('selectedTabIndex')) {
		api.updateVisibility('selectedTabIndex', false);
	}

	if (trigger === 'position' || !trigger) {
		if (position === 'top') {
			api.updateVisibility('maxWidth', true);
			api.updateVisibility('fixedWidth', true);
			api.updateVisibility('hidePadding', true);
			api.updateVisibility('spaceBetweenItems', false);
			api.updateVisibility('enableCollapsing', false);
			api.updateVisibility('initiallyCollapsed', false);
			api.updateVisibility('showInlinePadding', false);
		} else {
			api.updateVisibility('spaceBetweenItems', true);
			api.updateVisibility('enableCollapsing', true);
			api.updateVisibility('initiallyCollapsed', true);
			api.updateVisibility('showInlinePadding', true);
			api.updateVisibility('maxWidth', false);
			api.updateVisibility('fixedWidth', false);
			api.updateVisibility('hidePadding', false);
		}
	}
}

// eslint-disable-next-line no-unused-vars
function buttonStatefulPolicy({trigger, api, properties}) {
	if (trigger === 'disabled' || trigger === null) {
		api.updateVisibility('tooltipContent', !properties.disabled);
	}
}

// This file sets logical behavior for the now-predicate-builder config panel in UIB
// e.g. disabling properties that depending on other props, etc

//eslint-disable-next-line no-unused-vars
function nowPredicateBuilderPolicy(helpers) {
	const { api, trigger, properties } = helpers;
	const CONDITION_BUILDER_TYPE = 'CONDITION_BUILDER';

	const setPredicateBuilderSectionVisibility = (value) => {
		api.updateSectionVisibility('predicate-builder', value);
	};
	const setConditionBuilderSectionVisibility = (value) => {
		api.updateSectionVisibility('condition-builder', value);
		api.updateSectionVisibility('advanced', value);
	};
	// handle the case when the component is first loaded or when the builderType is changed
	if (!trigger || trigger === 'builderType') {
		if (properties?.builderType === CONDITION_BUILDER_TYPE) {
			setPredicateBuilderSectionVisibility(false);
			setConditionBuilderSectionVisibility(true);
		} else {
			setPredicateBuilderSectionVisibility(true);
			setConditionBuilderSectionVisibility(false);
		}
	}
}

// eslint-disable-next-line no-unused-vars
function snLegacyWidgetPolicy({trigger, api, properties}) {
	if (trigger === 'showHeader' || trigger === null) {
		const showingHeader = properties.showHeader;
		api.updateVisibility('bareBorder', !showingHeader); //we show the bare border toggle when the header is hidden
		api.updateVisibility('showTitle', showingHeader); //we show the title toggle when the header is visible
	}
}
