result.query:
query ($table: String!, $sysId: String, $view: String, $source: String!, $parentTable: String!, $parentSysId: String!, $playbookExperienceId: String, $activityDefinitionId: String, $workspaceConfigId: String) {
	GlideDeclarativeActions_Query {
		GlideDeclarativeActions_playbookQuery {
			playbookDeclarativeActions(table: $table, sysId: $sysId, view: $view, source: $source,  relatedParentRecordTable: $parentTable, relatedParentRecordSysId: $parentSysId, playbookExperienceId: $playbookExperienceId, activityDefinitionId: $activityDefinitionId, workspaceConfigId: $workspaceConfigId) {
				playbookActions {
					actions {
						actionAttributes
						actionComponent
						actionDispatch
						actionPayload
						actionType
						assignmentId
						buttonType
						conditions
						confirmationMessage
						confirmationRequired
						dependency
						groupBy
						group
						icon
						label
						modelFieldRequired
						name
						order
						payloadMap {
							name
							value
						}
						recordSelectionRequired
						formFieldsRequired
						requiresValue
						renderMode
						table
						tooltip
						view
						modelConditions {
							field
							operator
							value
							newQuery
							or
						}
					}
				}
			}
		}
	}
}

