var totalTime = 0;
var gr = new GlideRecord("sn_critical_update");
gr.addQuery("up_update_status", "acknowledged");
gr.query();
var totalAck = gr.getRowCount();
while (gr.next()) {
    var endDate = gr.sys_updated_on.getGlideObject();
    var grR = new GlideRecord("sys_audit");
    grR.addQuery("tablename", "sn_critical_update");
    grR.addQuery("fieldname", "up_active");
    grR.addQuery("documentkey", gr.sys_id);
    grR.orderByDesc("sys_created_on");
    grR.query();
    if (grR.next() && grR.newvalue == "1") {
        var startDate = grR.sys_created_on.getGlideObject();
        diff = gs.dateDiff(startDate.getDisplayValueInternal(), endDate.getDisplayValueInternal(), false);
        var days = diff.split(" ");
        if (days.length == 2) {
            totalTime += days[0];
        }
    }
}

answer = (totalTime / totalAck);

answer = JSON.stringify(solutionDefinitionsPerTemplate());

function solutionDefinitionsPerTemplate() {
	if (!haveRequiredTables())
		return 0;

	var obj = {};

	// Total solution definition count
	var count = 0;
	var ga = new GlideAggregate('ml_capability_definition_base');
	ga.addAggregate('COUNT');
	ga.addQuery('sys_created_on', '>=', gs.daysAgo(1));
	ga.query();
	if (ga.next()) {
		count = ga.getAggregate('COUNT');
	}
	obj.count = count;

	// Solution definitions per template type
	var solution_capability = [];
	ga = new GlideAggregate('ml_capability_definition_base');
	ga.addAggregate('COUNT', 'capability.value');
	ga.addQuery('sys_created_on', '>=', gs.daysAgo(1));
	ga.query();
	while (ga.next()) {
		var capability = {};
		capability.count = ga.getAggregate('COUNT', 'capability.value');
		capability.capability = ga.getValue('capability.value');
		solution_capability.push(capability);
	}
	obj.solution_capability = solution_capability;

	return obj;
}

function haveRequiredTables() {
	return GlideTableDescriptor.isValid('ml_capability_definition_base');
}

