function getPolicyCountUsingIPRoleAuthSchemeFilters() {
    var record = new GlideAggregate('sys_authentication_policy_criteria_m2m');
    record.addAggregate('COUNT');
    record.groupBy('policy');
    record.addQuery('used', 'true');
